import React,{ useState } from "react";
import './Button.css'

const Button = (props) => {
    const [turn, setTurn] = useState(props.turn_val=='Green');
    console.log(turn);
    return(
        <div className="button_container">
            <button id='roll_button' className='button'>Roll Dice: <span id="dice_val_button">{props.dice_val}</span></button>

        </div>
    );
}

export default Button;
