import React,{useState} from 'react';
import './App.css';
import Intro from './components/Intro';
import Board from './components/Board';
import Button from './components/Button';
import {x_val, y_val, check} from './functions';
import Counter from './components/Counter';

function App() {
  let speed = 400;
  var intervalId1;
  const [turn, setTurn] = useState(true);
  const [dice_val, setDice_val] = useState("...");
  const [space_enabled, setSpace_enabled] = useState(true);
  const [enter_enabled, setEnter_enabled] = useState(false);
  const [player1, setPlayer1] = useState(1);
  const [player2, setPlayer2] = useState(1);

  let variables = {
    turn: turn,
    setTurn: setTurn,
    dice_val: dice_val,
    setDice_val: setDice_val,
    space_enabled: space_enabled,
    enter_enabled: enter_enabled,
    player1: setPlayer1,
    player2: player2
  }

  const rollDice = ()=>{
    setDice_val(Math.round(1+(5)*Math.random()));
    setEnter_enabled(true);
  }

  const move1 = (setCounter) =>{
    let z = player1 + dice_val;
    let c = check(z);
    if(!turn || !enter_enabled) return;

    if(z<=100){ 
      setPlayer1(z);
      if(z===100){
        setTimeout(() => {
          alert("Green has won the match!! Refresh to Restart.");
        }, speed);
      }
      if(c){
        setTimeout(() => {
          setPlayer1(c);
          setCounter(c);
        }, speed);
      }
    }

    setEnter_enabled(false);
    setSpace_enabled(true);
    setTurn(false);
    setDice_val("...");
  }
  const move2 = (setCounter) =>{
    let z = player2 + dice_val;
    let c = check(z);
    if(turn || !enter_enabled)return;
    if(z<=100){
      setPlayer2(z);
      if(z==100){
        setTimeout(() => {
          alert("Red has won the match!! Refresh to Restart.");
        }, speed);
      }
      if(c){
        setTimeout(() => {
          setPlayer2(c);
          setCounter(c);
        }, speed);
      }
    } 
    setEnter_enabled(false);
    setSpace_enabled(true);
    setTurn(true);
    setDice_val("...");
  }

  const Clicked = () => {
    if(space_enabled){
      setSpace_enabled(false);
      rollDice();      
    }
  }

  return (
    <div>
      <Intro turn={turn} dice_val={dice_val}/>
      <Board 
        turn={turn} 
        enter_enabled={enter_enabled} 
        player1={player1} 
        player2={player2}
        move1={move1}
        move2={move2}
        dice_val={dice_val}
      />
      <Button turn={turn} dice_val={dice_val} onClickRollDice={Clicked}/>
    </div>
  );
}

export default App;
