    var intervalId1 = setInterval(lets_move, 500);
    var z = 6;


    const Green_marker = {
        margin:'35% 40% 15% 10%',
        /* margin: 25% 10% 15% 30%; */
        /* position: absolute; */
        backgroundColor: 'rgb(14, 221, 66)',
        borderRadius: '50%',
        border: '1px solid rgb(0, 0, 0)',
        gridRowStart: props.position.x,
        gridColumnStart: limit,
        transition: "all 2s ease-in"
    };
    return(
        <div 
            id='pos1'
            style={Green_marker}
            className={props.turn && props.enter_enabled ?"blinker1":""}
            onClick={props.move}
        />


    );
}
export default Green
