    // setDice_val("...");
    move_red(variables, setCounter);
  }

  const move = (setCounter) => {
    if(turn)
      move_green(variables, )
  }

  const Clicked = () => {
    if(space_enabled){
      setSpace_enabled(false);
      rollDice();      
    }
  }

  return (
    <div>
      <Intro turn={turn} dice_val={dice_val}/>
      <Board 
        turn={turn} 
        enter_enabled={enter_enabled} 
        player1={player1} 
        player2={player2}
        move1={move1}
        move2={move2}
        dice_val={dice_val}
      />
      <Button turn={turn} dice_val={dice_val} onClickRollDice={Clicked}/>
    </div>
  );
}

export default App;





