import React from "react";
import { useState } from "react";
import './Board.css'
import { x_val, y_val } from "../functions";





const Green = (props) => {



    // function lets_move (){
    //     console.log(limit, z, counter);
    //     if(limit===z){
    //         window.clearInterval(intervalId1);
    //         console.log("inside");
    //     }
    //     limit = (limit+1);
    //     setCounter(limit);
    // }


    // // const [limit, setLimit] = useState(0);
    // let limit = 1;
    // const [counter, setCounter] = useState(1);
    // const intervalId1 = setInterval(lets_move, 3000);
    var z = 12;


    const [counter, setCounter] = React.useState(1);
    const [move_on, setMove_on] = useState(true);
    var timer;

    function temp(){
        if(move_on){
            timer = setInterval(() => {
                setCounter(prevCount => prevCount + 1); // <-- Change this line!
            }, 500);
        }
        else{
            clearInterval(timer)
        }
        return () => {
        clearInterval(timer);
        };
    }

    React.useEffect(temp, []); // Pass in empty array to run effect only once!




    // console.log(counter);
    if(counter===z){
        console.log("entered");
        console.log(timer);
        setMove_on(false)
    }

    const Green_marker = {
        margin:'35% 40% 15% 10%',
        /* margin: 25% 10% 15% 30%; */
        /* position: absolute; */
        backgroundColor: 'rgb(14, 221, 66)',
        borderRadius: '50%',
        border: '1px solid rgb(0, 0, 0)',
        gridRowStart: x_val(counter),
        gridColumnStart: y_val(counter),
        transition: "all 2s ease-in"
    };


    return(
        <div 
            id='pos1'
            style={Green_marker}
            className={props.turn && props.enter_enabled ?"blinker1":""}
            onClick={props.move}
        />


    );
}
export default Green
