import React,{ useState } from "react";
import './Board.css'
import Red from "./Red";
import Green from "./Green";

const Board = (props) => {

    const board_green = {
        border:"2px solid rgb(11, 236, 67)"
    };
    const board_red = {
        border:"2px solid rgb(241, 34, 34)"
    };
    var intervalId1;
    var intervalId2;



    const move1 = () =>{
        document.getElementById('pos1').gridRowStart=props.pla
    }

    return (
        <div className="container" id="board_container">
            <div id="board" style={props.turn?board_green:board_red }>
                <Green 
                    position={props.player1} 
                    enter_enabled={props.enter_enabled}
                    turn={props.turn}
                    move={props.move1}
                    dice_val={props.dice_val}
                />
                <Red 
                    position={props.player2}
                    enter_enabled={props.enter_enabled}
                    turn={props.turn}
                    move={props.move2}
                    dice_val={props.dice_val}
                />
            </div>
        </div>
    );
}

export default Board;
