const x_val = (z) => {
    // x_val(z) takes the z co-ordinate as input and returns its corresponding 
    // value of 'x'
    return 10-Math.floor((z-1)/10);
} 


const y_val = (z) => {
    // y_val(z) takes the z co-ordinate as input and returns its corresponding 
    // value of 'y'
    let temp = Math.floor((z-1)/10);
    if(temp%2===0){
        if(z%10==0)
            return 10;
        return z%10;
    }
    else{
        if(z%10==0)
            return 1;
        return 11-z%10;
    }
} 


const move_ = (player1, dice_val, setPlayer1, setEnter_enabled, setSpace_enabled, setTurn, setDice_val, setCounter) =>{
    let z = player1 + dice_val;
    let c = check(z);
    if(!turn || !enter_enabled) return;

    if(z<=100){ 
      setPlayer1(z);
      if(z===100){
        setTimeout(() => {
          alert("Green has won the match!! Refresh to Restart.");
        }, speed);
      }
      if(c){
        setTimeout(() => {
          setPlayer1(c);
          setCounter(c);
        }, speed);
      }
    }

    setEnter_enabled(false);
    setSpace_enabled(true);
    setTurn(false);
    setDice_val("...");
  }



const check_map = new Map([
    [7,25],
    [19,39],
    [41,18],
    [47,16],
    [46,68],
    [60,79],
    [72,13],
    [73,94],
    [87,56],
    [95,76],
    [99,27]
]);

const check = (z) =>{
    if(check_map.has(z))
        return check_map.get(z);
    else {
        return 0;
    }
}


export  {x_val,y_val, check};
