import React from "react";
import { useState } from "react";
import './Board.css'
import { x_val, y_val } from "../functions";





const Green = (props) => {



    // function lets_move (){
    //     console.log(limit, z, counter);
    //     if(limit===z){
    //         window.clearInterval(intervalId1);
    //         console.log("inside");
    //     }
    //     limit = (limit+1);
    //     setCounter(limit);
    // }


    // // const [limit, setLimit] = useState(0);
    // let limit = 1;
    // const [counter, setCounter] = useState(1);
    // const intervalId1 = setInterval(lets_move, 3000);
    var z = props.position.z + props.dice_val;


    const [counter, setCounter] = React.useState(props.position.z);
    const [moveOn, setMoveOn] = useState(false);

    React.useEffect(() => {
      let interval = null;

      if(moveOn){
        interval = setInterval(() => {
            setCounter((prevCounter) => prevCounter + 1);
        }, 400);
      }
      else{
          clearInterval(interval);
          props.move(setCounter);
        //   setCounter(props.position.z);
      }

      return () => {
        clearInterval(interval);
      };
    }, [moveOn]);





    // console.log(counter);
    if(moveOn && counter===z){
        // console.log("entered");
        setMoveOn(false);
    }

    const slide = () => {
        if(z>100) {
            setMoveOn(false); 
            return;
        }
        console.log("clicked & dice_val: ", z);
        if(props.turn && props.enter_enabled)
            setMoveOn(true);
    }

    const Green_marker = {
        margin:'35% 40% 15% 10%',
        /* margin: 25% 10% 15% 30%; */
        /* position: absolute; */
        backgroundColor: 'rgb(14, 221, 66)',
        borderRadius: '50%',
        border: '1px solid rgb(0, 0, 0)',
        gridRowStart: x_val(counter),
        gridColumnStart: y_val(counter),
        transition: "all 2s ease-in"
    };


    return(
        <div 
            id='pos1'
            style={Green_marker}
            className={props.turn && props.enter_enabled ?"blinker1":""}
            onClick={slide}
        />


    );
}
export default Green
