import React,{useState} from 'react';
import './App.css';
import Intro from './components/Intro';
import Board from './components/Board';
import Button from './components/Button';
import {x_val, y_val, check, move_green, move_red} from './functions';
import Counter from './components/Counter';


const dummyCoins = [
  {
    id: 'Green',
    margin: '35% 40% 15% 10%',
    backgroundColor: 'rgb(14, 221, 66)',
  },
  {
    id: 'Red',
    margin:'35% 10% 15% 40%',
    backgroundColor: 'rgb(241, 34, 34)',
  },
  {
    id: 'Blue',
    margin: '15% 25% 35% 25%',
    backgroundColor: 'blue',
  },
  {
    id: 'Yellow',
    margin: '45% 25% 5% 25%',
    backgroundColor: 'yellow',
  }
];

function App() {


  const [N, setN] = useState(0);


  React.useEffect(() => {
    if(!N){
      let n = 0;
      while((n!=2) && (n!=3) && (n!=4))
        n = prompt("Enter the number of players from 2 to 4", "2");
      setN(n);
    }
  }, [N]);


  let speed = 200;
  const [turn, setTurn] = useState(0);
  const [diceVal, setDiceVal] = useState("...");
  const [spaceEnabled, setSpaceEnabled] = useState(true);
  const [enterEnabled, setEnterEnabled] = useState(false);
  const [player1, setPlayer1] = useState(1);
  const [player2, setPlayer2] = useState(1);
  const [positions, setPositions] = useState([1,3,8,1]);

  let variables = {
    turn: turn,
    setTurn: setTurn,
    diceVal: diceVal,
    setDiceVal: setDiceVal,
    spaceEnabled: spaceEnabled,
    enterEnabled: enterEnabled,
    setEnterEnabled: setEnterEnabled,
    setSpaceEnabled: setSpaceEnabled,
    player1: player1,
    player2: player2,
    setPlayer1: setPlayer1,
    setPlayer2: setPlayer2,
    speed: speed, 
    N:N,
    positions: positions,
    setPositions: setPositions,
  }

  const rollDice = ()=>{
    setDiceVal(Math.round(1+(5)*Math.random()));
    // setDiceVal(6);
    setEnterEnabled(true);
  }

  const move = (setCounter) => {
    if(turn)
      move_green(variables, setCounter);
    else
      move_red(variables, setCounter);
  }

  const Clicked = () => {
    if(spaceEnabled){
      setSpaceEnabled(false);
      rollDice();      
    }
  }

  return (
    <div>
      <Intro turn={turn} diceVal={diceVal}/>
      <Board 
        turn={turn} 
        enterEnabled={enterEnabled} 
        player1={player1} 
        player2={player2}
        move = {move}
        diceVal={diceVal}
        speed = {speed}
        coins={dummyCoins}
        N = {N}
        positions={positions}
      />
      <Button turn={turn} diceVal={diceVal} onClickRollDice={Clicked}/>
    </div>
  );
}

export default App;





