import React,{useState} from 'react';
import './App.css';
import Intro from './components/Intro';
import Board from './components/Board';
import Button from './components/Button';
import {x_val, y_val, check} from './functions';

function App() {
  let speed = 400;
  const [turn, setTurn] = useState(true);
  const [dice_val, setDice_val] = useState("...");
  const [space_enabled, setSpace_enabled] = useState(true);
  const [enter_enabled, setEnter_enabled] = useState(false);
  const [player1, setPlayer1] = useState({x:10, y:1, z:1});
  const [player2, setPlayer2] = useState({x:10, y:1, z:1});

  const rollDice = ()=>{
    setDice_val(Math.round(1+(5)*Math.random()));
    setEnter_enabled(true);
  }

  const move1 = () =>{
    let z = player1.z+dice_val;
    let c = check(z);
    if(!turn || !enter_enabled) return;

    if(z<=100){ 
      const updatedState = {}, range = dice_val, p=player1.z;
      for (let i = 1; i <= range; i++) {
        updatedState[i] = '';
        setTimeout(() => {
          setPlayer1({
            x: x_val(z),
            y: y_val(z),
            z: z

          });
        }, speed);

      }
      // setPlayer1({
      //   x: x_val(z),
      //   y: y_val(z),
      //   z: z
      // });
      if(z===100){
        setTimeout(() => {
          alert("Green has won the match!! Refresh to Restart.");
        }, speed);
      }
      if(c){
        setTimeout(() => {
          setPlayer1({
            x: x_val(c),
            y: y_val(c),
            z: c
          });
        }, speed);
      }
    }

    setEnter_enabled(false);
    setSpace_enabled(true);
    setTurn(false);
    setDice_val("...");
  }
  const move2 = () =>{
    let z = player2.z+dice_val;
    let c = check(z);
    if(turn || !enter_enabled)return;
    if(z<=100){
      setPlayer2({
        x: x_val(player2.z + dice_val),
        y: y_val(player2.z + dice_val),
        z: player2.z + dice_val
      });
      if(z==100){
        setTimeout(() => {
          alert("Red has won the match!! Refresh to Restart.");
        }, speed);
      }
      if(c){
        setTimeout(() => {
          setPlayer2({
            x: x_val(c),
            y: y_val(c),
            z: c
          });
        }, speed);
      }
    } 
    setEnter_enabled(false);
    setSpace_enabled(true);
    setTurn(true);
    setDice_val("...");
  }

  const Clicked = () => {
    if(space_enabled){
      setSpace_enabled(false);
      rollDice();      
    }
  }

  return (
    <div>
      <Intro turn={turn} dice_val={dice_val}/>
      <Board 
        turn={turn} 
        enter_enabled={enter_enabled} 
        player1={player1} 
        player2={player2}
        move1={move1}
        move2={move2}
      />
      <Button turn={turn} dice_val={dice_val} onClickRollDice={Clicked}/>
    </div>
  );
}

export default App;
