import React,{useState} from 'react';
import './App.css';
import Intro from './components/Intro';
import Board from './components/Board';
import Button from './components/Button';
// import Clicked from './functions';

function App() {
  const [speed, setSpeed] = useState(200);
  const [turn, setTurn] = useState(true);
  const [dice_val, setDice_val] = useState(0);
  const [space_enabled, setSpace_enabled] = useState(true);
  const [enter_enabled, setEnter_enabled] = useState(false);
  const [player1, setPlayer1] = useState({x:10, y:1, z:1});
  const [player2, setPlayer2] = useState({x:10, y:1, z:1});

  const Clicked = () => {
    console.log('roller clicked');
    if(space_enabled){
      setSpace_enabled(false);
    }
    // if(turn)
    //   setTurn(false)
    // else
    //   setTurn(true);
  }

  return (
    <div>
      <Intro turn={turn} dice_val={dice_val}/>
      <Board turn={turn} player1={player1} player2={player2}/>
      <Button turn={turn} dice_val={dice_val} onClickRollDice={Clicked}/>
    </div>
  );
}

export default App;
