import React,{useState} from 'react';
import './App.css';
import Intro from './components/Intro';
import Board from './components/Board';
import Button from './components/Button';
import {x_val, y_val} from './functions';

function App() {
  const [speed, setSpeed] = useState(200);
  const [turn, setTurn] = useState(true);
  const [dice_val, setDice_val] = useState("...");
  const [space_enabled, setSpace_enabled] = useState(true);
  const [enter_enabled, setEnter_enabled] = useState(false);
  const [player1, setPlayer1] = useState({x:10, y:1, z:1});
  const [player2, setPlayer2] = useState({x:10, y:1, z:1});

  const rollDice = ()=>{
    let a = 1;
    let b = 6;
    setDice_val(Math.round(a+(b-a)*Math.random()));
    // console.log(dice_val);
    setEnter_enabled(true);
    click_div();
  }

  const click_div = () => {
    if(enter_enabled){
      setEnter_enabled(false);
      console.log('Enter');
      // gameEngine();
    }
  }

  const move1 = () =>{
    let c = dice_val;
    while(c--){}
    setPlayer1({
      x: x_val(player1.z + dice_val),
      y: y_val(player1.z + dice_val),
      z: player1.z + dice_val
    });
    setEnter_enabled(false);
    setSpace_enabled(true);
    setTurn(false);
    setDice_val("...");
  }
  const move2 = () =>{
    setPlayer2({
      x: x_val(player2.z + dice_val),
      y: y_val(player2.z + dice_val),
      z: player2.z + dice_val
    });
    setEnter_enabled(false);
    setSpace_enabled(true);
    setTurn(true);
    setDice_val("...");
  }

  const Clicked = () => {
    console.log('roller clicked');
    if(space_enabled){
      setSpace_enabled(false);
      rollDice();      
    }

    // if(turn)
    //   setTurn(false)
    // else
    //   setTurn(true);
  }

  return (
    <div>
      <Intro turn={turn} dice_val={dice_val}/>
      <Board 
        turn={turn} 
        enter_enabled={enter_enabled} 
        player1={player1} 
        player2={player2}
        move1={move1}
        move2={move2}
      />
      <Button turn={turn} dice_val={dice_val} onClickRollDice={Clicked}/>
    </div>
  );
}

export default App;
