import React, { useState } from "react";
import './Intro.css'

const Intro = (props) =>{



    return (
        <div className="intro">
        <h3>
            TURN: 
            <span id="turn">{props.turn ? 'Green':'Red'}</span> 
            DICE-VALUE:  
            <span id="dice_val">{props.dice_val}</span>
        </h3>
        <h3><span id="roller">Press Space to Roll the Dice.</span></h3>
    </div>
    )
}

export default Intro;
