import React,{ useState } from "react";
import './Board.css'
import Red from "./Red";
import Green from "./Green";
import Coin from "./Coin";
import Coin2 from "./Coin2";

const Board = (props) => {

    const board_green = {
        border:"2px solid rgb(11, 236, 67)"
    };
    const board_red = {
        border:"2px solid rgb(241, 34, 34)"
    };

    const board_color = [{border:"2px solid rgb(11, 236, 67)"},
                        {border:"2px solid rgb(241, 34, 34)"},
                        {border:"2px solid blue"}, 
                        {border:"2px solid red"}];

    let N = props.N;
    // console.log(props.coins);

    const filteredCoins = props.coins.filter((coin) => {

        return props.coins.indexOf(coin) < N;
    });

    // console.log(N,filteredCoins);

    let i = 0;


    return (
        <div className="container" id="board_container">
            <div id="board" style={board_color[props.turn]}>

                {filteredCoins.map((coin)=>(
                    <Coin 
                        key={coin.id}
                        margin={coin.margin}
                        backgroundColor={coin.backgroundColor}
                        position={props.positions[filteredCoins.indexOf(coin)]}
                        enterEnabled={props.enterEnabled}
                        turn={filteredCoins.indexOf(coin)==props.turn}
                        move={props.move}
                        diceVal={props.diceVal}
                        speed = {props.speed}
                    />

                ))}

                {/* <Green 
                    position={props.player1} 
                    enterEnabled={props.enterEnabled}
                    turn={props.turn}
                    move={props.move}
                    diceVal={props.diceVal}
                    speed = {props.speed}
                />
                <Red 
                    position={props.player2}
                    enterEnabled={props.enterEnabled}
                    turn={!props.turn}
                    move={props.move}
                    diceVal={props.diceVal}
                    speed = {props.speed}
                /> */}
                {/* <Coin 
                    position={props.player2}
                    enterEnabled={props.enterEnabled}
                    turn={!props.turn}
                    move={props.move}
                    diceVal={props.diceVal}
                    speed = {props.speed}
                />
                <Coin2 
                    position={props.player2}
                    enterEnabled={props.enterEnabled}
                    turn={!props.turn}
                    move={props.move}
                    diceVal={props.diceVal}
                    speed = {props.speed}
                /> */}
            </div>
        </div>
    );
}

export default Board;
