import React from "react";





const Red = (props) => {

    var speed = 400;
    var z = props.position.z + props.dice_val;
    const [counter, setCounter] = useState(props.position.z);
    const [moveOn, setMoveOn] = useState(false);

    React.useEffect(() => {
        let interval = null;
        if (moveOn) {
            interval = setInterval(() => {
                setCounter((prevCounter) => prevCounter + 1);
            }, speed);
        }
        else {
            clearInterval(interval);
            props.move(setCounter);
        }
        return () => {
            clearInterval(interval);
        };
    }, [moveOn]);

    if (moveOn && counter === z) {        
        setMoveOn(false);
    }

    const slide = () => {
        if (z > 100) { props.move(); return; }

        if (props.turn && props.enter_enabled)
            setMoveOn(true);
    }

    const Red_marker = {
        margin:'35% 10% 15% 40%',
        /* margin: 25% 10% 15% 30%; */
        /* position: absolute; */
        backgroundColor: 'rgb(241, 34, 34)',
        borderRadius: '50%',
        border: '1px solid rgb(0, 0, 0)',
        gridRowStart: props.position.x,
        gridColumnStart: props.position.y,
    };


    return(
        <div 
            id='pos2'
            style={Red_marker}
            className={props.turn && props.enter_enabled ?"blinker2":""}
            onClick={props.move}
        />
    );
}
export default Red
